svg.script:
const toggleFill = (e) => {
    const path = e.currentTarget;
    const fill = path.getAttribute('fill');
    const dataFill = path.getAttribute('data-fill');
    path.setAttribute('fill', dataFill);
    path.setAttribute('data-fill', fill);
}

const customFrenchFlagSvg = document.getElementById('custom-france-flag');
const paths = customFrenchFlagSvg.querySelectorAll('path');
console.log(paths);
paths.forEach((path) => path.addEventListener('click', toggleFill));
